function updateQuote(){var a=Math.floor(Math.random()*quotes.length);document.getElementById("quote-body").innerHTML=quotes[a][0];document.getElementById("quote-src").innerHTML=quotes[a][1];document.getElementById("quote-src").href=quotes[a][2];document.getElementById("quote").style.opacity=1}function main(){window.setTimeout(updateQuote,1000);window.setInterval(function(){document.getElementById("quote").style.opacity=0;window.setTimeout(updateQuote,1000)},15000)}if(document.readyState==="complete"){main()}else{window.addEventListener("load",main)};